import Vue from "vue"
import VueRouter from "vue-router"
import Home from "@/views/Home.vue"
import List from "@/views/List.vue"
import Cart from "@/views/Cart.vue"
import My from "@/views/My.vue"

const originalPush = VueRouter.prototype.push;
const originalReplace = VueRouter.prototype.replace;

// push
VueRouter.prototype.push = function push(location, onResolve, onReject) {
  if (onResolve || onReject)
    return originalPush.call(this, location, onResolve, onReject);
  return originalPush.call(this, location).catch(err => err);
};

//replace
VueRouter.prototype.replace = function push(location, onResolve, onReject) {
  if (onResolve || onReject)
    return originalReplace.call(this, location, onResolve, onReject);
  return originalReplace.call(this, location).catch(err => err);
};


mode: 'history'
Vue.use(VueRouter)
const routes = [{
        path: '/home',
        name: 'Home',
        component: Home,
    },
    {
        path: '/',
        redirect: '/home'
    },
    {
        path: '/list',
        name: 'List  ',
        component: () =>
            import('@/views/List.vue')
    },
    {
        path: '/cart',
        name: 'Cart',
        component: () =>
            import('@/views/Cart.vue')
    },
    {
        path: '/my',
        name: 'My',
        component: () =>
            import('@/views/My.vue')
    },
    {
        path: '/search',
        name: 'Search',
        children: [{
                path: '/',
                name: 'index',
                component: () =>
                    import('@/views/search/Search-index.vue')
            },
            {
                path: 'list',
                name: 'list',
                component: () =>
                    import('@/views/search/Search-list.vue')
            }
        ],
        component: () =>
            import('@/views/Search.vue')
    },
    {
        path: '/detail',
        name: 'Detail',
        meta: {
            keepAlive: true
        },
        component: () =>
            import('@/views/Detail.vue')
    },
    {
        path: '/login',
        name: "Login",
        component: () =>
            import('@/views/login/Login.vue')
    },
    {
        path: '/userLogin',
        name: "UserLogin",
        component: () =>
            import('@/views/login/UserLogin.vue')
    },
    {
        path: '/register',
        name: "Register",
        component: () =>
            import('@/views/login/Register.vue')
    },
    {
        path: "/recovery",
        name: "Recovery",
        component: () =>
            import("@/views/recovery/RecoveryIndex.vue"),
    },
    {
        path: "/recoverybtn",
        name: "Recoverybtn",
        component: () =>
            import("@/views/recovery/RecoveryBtn.vue"),
    },
    {
        path: "/path",
        name: "Path",
        component: () =>
            import("@/views/path/Path-Index.vue"),
    },
    {
        path: "/path-list",
        name: "path-list",
        component: () =>
            import("@/views/path/Path-List.vue"),
    },
    {
        path: "/order",
        name: "Order",
        meta:{
            keepAlive:true
        },
        component: () =>
            import("../views/Order.vue"),
    },
    {
        path: "/payment",
        name: "Payment",
        component: () =>
          import("../views/Payment.vue"),
      },


]
const router = new VueRouter({
    mode: "history",
    base: process.env.BASE_URL,
    routes
});


router.beforeEach((to,from,next)=>{
    let nextRouter = ['Payment','Cart','Path','Order','path-list']
    let userInfo = JSON.parse(localStorage.getItem('teaUserInfo'))
    if(nextRouter.indexOf(to.name)>=0){
        if(!userInfo){
            router.push('/login')
        }
    }
    next()
})



export default router
