import { INIT_DATA } from './mutations-type.js'
export default{
    state:{
        list:[]
    },
    getters:{
        defaultPath( state ){
            // 筛选出默认地址，如果是1就返回出来
            return state.list.filter(v=>{
                return v.isDefault == 1;
            })
        }
    },
    mutations:{
        [INIT_DATA]( state , arrPath ){
            state.list = arrPath;
        }
    }
}
